package com.cmb;

import com.facebook.react.modules.core.DeviceEventManagerModule;

public class RCTCmbEvent {

    public enum CMBEvent {
        READ_RESULT_RECEIVED ("didReceiveReadResultFromReaderEvent"),
        AVAILABILITY_CHANGED ("availabilityDidChangeOfReaderEvent"),
        CONNECTION_STATE_CHANGED ("connectionStateDidChangeOfReaderEvent"),
        SCANNING_STATE_CHANGED ("scanningStateChangedEvent"),
        COMMAND_COMPLETED ("cmbCommandCompletionEvent");

        private final String name;

        CMBEvent(String s) {
            name = s;
        }

        public String toString() {
            return this.name;
        }
    }

    public enum CMBEventKey {
        CMD_ID ("commandID"),
        CMD ("command"),
        SUCCESS ("success"),
        MESSAGE ("message"),
        EVENT_TYPE ("eventType");

        private final String name;

        CMBEventKey(String s) {
            name = s;
        }

        public String toString() {
            return this.name;
        }
    }

    CMBEvent event;
    Object genericBody;

    public RCTCmbEvent(CMBEvent event, Object body) {
        this.event = event;
        this.genericBody = body;
    }

    public void sendUsingEmitter(DeviceEventManagerModule.RCTDeviceEventEmitter emitter) {
        emitter.emit(event.name, genericBody);
    }

}
